import { createStore } from 'vuex'

const store = createStore({
  state(){
    return{
      data:"",
      todoThings:[]
    }
  },
  getters: {
  },
  mutations: {
    sendTodo(state,params) {
      state.todoThings.push(params)
    },
    deleteThings(state,params){
      state.todoThings.splice(params,1)
    }
  },
  actions: {
  },
  modules: {
  }
})
export default store
